class SocialLink{constructor(t,e){this.element=t,this.socialNetwork=e,this.title=encodeURIComponent(document.title),this.url=encodeURIComponent(window.location.href),this.setHref(),this.element.addEventListener("click",this.clickHandler.bind(this))}setHref(){this.element.setAttribute("href",this.getSharingUrl(this.socialNetwork))}getSharingUrl(t){switch(t){case"facebook":return`https://www.facebook.com/sharer.php?u=${this.url}`;case"twitter":return`https://twitter.com/share?url=${this.url}&text=${this.title}`;case"linkedin":return`https://www.linkedin.com/shareArticle?mini=true&url=${this.url}&title=${this.title}`}}clickHandler(t){t.preventDefault(),this.openWindow(t.currentTarget.href)}openWindow(t,e="Share Post",i=600,n=500){const r=`status=1,width=${i},height=${n},top=${(window.innerHeight-n)/2},left=${(window.innerWidth-i)/2}`;window.open(t,e,r)}}const initSocialLinks=()=>{Array.from(document.querySelectorAll(".js-social")).forEach((t=>new SocialLink(t,t.getAttribute("data-network"))))};window.addEventListener("DOMContentLoaded",initSocialLinks);